CREATE DEFINER=`root`@`localhost` PROCEDURE `check_login_info`(IN username_var varchar(50), IN password_var varchar(50))
BEGIN
	SELECT u.user_id 
      FROM user AS u 
      JOIN login_info AS li 
      USING (user_id) 
      WHERE 
      BINARY li.username = username_var 
      AND BINARY li.password = password_var;
END ;;
DELIMITER ;
