DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `get_last_comment`()
BEGIN
	SELECT 
        c.body AS body,
        c.comment_id AS comment_id,
        c.user_id AS user_id, 
        CONCAT(u.first_name, ' ', u.last_name) AS commenter_name, 
        u.email AS commenter_email 
      FROM comment AS c 
      JOIN user AS u 
        USING (user_id) 
      WHERE c.comment_id = LAST_INSERT_ID();
END ;;
DELIMITER ;
