CREATE DEFINER=`root`@`localhost` PROCEDURE `get_user_posts`(IN id_var int)
BEGIN
	SELECT 
        p.title AS title, 
        p.body AS body,
        p.post_id AS post_id,
        u.user_id AS user_id,
        CONCAT(u.first_name, ' ', u.last_name) AS full_name
      FROM post AS p
      JOIN user AS u
      USING (user_id)
      WHERE p.user_id = id_var;
END ;;
DELIMITER ;
